// Create a JSON object only if one does not already exist. We create the
// methods in a closure to avoid creating global variables.

if (typeof JSON !== 'object') {
    JSON = {};
}

(function () {
    'use strict';

    function f(n) {
        // Format integers to have at least two digits.
        return n < 10 ? '0' + n : n;
    }

    if (typeof Date.prototype.toJSON !== 'function') {

        Date.prototype.toJSON = function () {

            return isFinite(this.valueOf())
                ? this.getUTCFullYear()     + '-' +
                    f(this.getUTCMonth() + 1) + '-' +
                    f(this.getUTCDate())      + 'T' +
                    f(this.getUTCHours())     + ':' +
                    f(this.getUTCMinutes())   + ':' +
                    f(this.getUTCSeconds())   + 'Z'
                : null;
        };

        String.prototype.toJSON      =
            Number.prototype.toJSON  =
            Boolean.prototype.toJSON = function () {
                return this.valueOf();
            };
    }

    var cx = /[\u0000\u00ad\u0600-\u0604\u070f\u17b4\u17b5\u200c-\u200f\u2028-\u202f\u2060-\u206f\ufeff\ufff0-\uffff]/g,
        escapable = /[\\\"\x00-\x1f\x7f-\x9f\u00ad\u0600-\u0604\u070f\u17b4\u17b5\u200c-\u200f\u2028-\u202f\u2060-\u206f\ufeff\ufff0-\uffff]/g,
        gap,
        indent,
        meta = {    // table of character substitutions
            '\b': '\\b',
            '\t': '\\t',
            '\n': '\\n',
            '\f': '\\f',
            '\r': '\\r',
            '"' : '\\"',
            '\\': '\\\\'
        },
        rep;


    function quote(string) {

// If the string contains no control characters, no quote characters, and no
// backslash characters, then we can safely slap some quotes around it.
// Otherwise we must also replace the offending characters with safe escape
// sequences.

        escapable.lastIndex = 0;
        return escapable.test(string) ? '"' + string.replace(escapable, function (a) {
            var c = meta[a];
            return typeof c === 'string'
                ? c
                : '\\u' + ('0000' + a.charCodeAt(0).toString(16)).slice(-4);
        }) + '"' : '"' + string + '"';
    }


    function str(key, holder) {

// Produce a string from holder[key].

        var i,          // The loop counter.
            k,          // The member key.
            v,          // The member value.
            length,
            mind = gap,
            partial,
            value = holder[key];

// If the value has a toJSON method, call it to obtain a replacement value.

        if (value && typeof value === 'object' &&
                typeof value.toJSON === 'function') {
            value = value.toJSON(key);
        }

// If we were called with a replacer function, then call the replacer to
// obtain a replacement value.

        if (typeof rep === 'function') {
            value = rep.call(holder, key, value);
        }

// What happens next depends on the value's type.

        switch (typeof value) {
        case 'string':
            return quote(value);

        case 'number':

// JSON numbers must be finite. Encode non-finite numbers as null.

            return isFinite(value) ? String(value) : 'null';

        case 'boolean':
        case 'null':

// If the value is a boolean or null, convert it to a string. Note:
// typeof null does not produce 'null'. The case is included here in
// the remote chance that this gets fixed someday.

            return String(value);

// If the type is 'object', we might be dealing with an object or an array or
// null.

        case 'object':

// Due to a specification blunder in ECMAScript, typeof null is 'object',
// so watch out for that case.

            if (!value) {
                return 'null';
            }

// Make an array to hold the partial results of stringifying this object value.

            gap += indent;
            partial = [];

// Is the value an array?

            if (Object.prototype.toString.apply(value) === '[object Array]') {

// The value is an array. Stringify every element. Use null as a placeholder
// for non-JSON values.

                length = value.length;
                for (i = 0; i < length; i += 1) {
                    partial[i] = str(i, value) || 'null';
                }

// Join all of the elements together, separated with commas, and wrap them in
// brackets.

                v = partial.length === 0
                    ? '[]'
                    : gap
                    ? '[\n' + gap + partial.join(',\n' + gap) + '\n' + mind + ']'
                    : '[' + partial.join(',') + ']';
                gap = mind;
                return v;
            }

// If the replacer is an array, use it to select the members to be stringified.

            if (rep && typeof rep === 'object') {
                length = rep.length;
                for (i = 0; i < length; i += 1) {
                    if (typeof rep[i] === 'string') {
                        k = rep[i];
                        v = str(k, value);
                        if (v) {
                            partial.push(quote(k) + (gap ? ': ' : ':') + v);
                        }
                    }
                }
            } else {

// Otherwise, iterate through all of the keys in the object.

                for (k in value) {
                    if (Object.prototype.hasOwnProperty.call(value, k)) {
                        v = str(k, value);
                        if (v) {
                            partial.push(quote(k) + (gap ? ': ' : ':') + v);
                        }
                    }
                }
            }

// Join all of the member texts together, separated with commas,
// and wrap them in braces.

            v = partial.length === 0
                ? '{}'
                : gap
                ? '{\n' + gap + partial.join(',\n' + gap) + '\n' + mind + '}'
                : '{' + partial.join(',') + '}';
            gap = mind;
            return v;
        }
    }

// If the JSON object does not yet have a stringify method, give it one.

    if (typeof JSON.stringify !== 'function') {
        JSON.stringify = function (value, replacer, space) {

// The stringify method takes a value and an optional replacer, and an optional
// space parameter, and returns a JSON text. The replacer can be a function
// that can replace values, or an array of strings that will select the keys.
// A default replacer method can be provided. Use of the space parameter can
// produce text that is more easily readable.

            var i;
            gap = '';
            indent = '';

// If the space parameter is a number, make an indent string containing that
// many spaces.

            if (typeof space === 'number') {
                for (i = 0; i < space; i += 1) {
                    indent += ' ';
                }

// If the space parameter is a string, it will be used as the indent string.

            } else if (typeof space === 'string') {
                indent = space;
            }

// If there is a replacer, it must be a function or an array.
// Otherwise, throw an error.

            rep = replacer;
            if (replacer && typeof replacer !== 'function' &&
                    (typeof replacer !== 'object' ||
                    typeof replacer.length !== 'number')) {
                throw new Error('JSON.stringify');
            }

// Make a fake root object containing our value under the key of ''.
// Return the result of stringifying the value.

            return str('', {'': value});
        };
    }


// If the JSON object does not yet have a parse method, give it one.

    if (typeof JSON.parse !== 'function') {
        JSON.parse = function (text, reviver) {

// The parse method takes a text and an optional reviver function, and returns
// a JavaScript value if the text is a valid JSON text.

            var j;

            function walk(holder, key) {

// The walk method is used to recursively walk the resulting structure so
// that modifications can be made.

                var k, v, value = holder[key];
                if (value && typeof value === 'object') {
                    for (k in value) {
                        if (Object.prototype.hasOwnProperty.call(value, k)) {
                            v = walk(value, k);
                            if (v !== undefined) {
                                value[k] = v;
                            } else {
                                delete value[k];
                            }
                        }
                    }
                }
                return reviver.call(holder, key, value);
            }


// Parsing happens in four stages. In the first stage, we replace certain
// Unicode characters with escape sequences. JavaScript handles many characters
// incorrectly, either silently deleting them, or treating them as line endings.

            text = String(text);
            cx.lastIndex = 0;
            if (cx.test(text)) {
                text = text.replace(cx, function (a) {
                    return '\\u' +
                        ('0000' + a.charCodeAt(0).toString(16)).slice(-4);
                });
            }

// In the second stage, we run the text against regular expressions that look
// for non-JSON patterns. We are especially concerned with '()' and 'new'
// because they can cause invocation, and '=' because it can cause mutation.
// But just to be safe, we want to reject all unexpected forms.

// We split the second stage into 4 regexp operations in order to work around
// crippling inefficiencies in IE's and Safari's regexp engines. First we
// replace the JSON backslash pairs with '@' (a non-JSON character). Second, we
// replace all simple value tokens with ']' characters. Third, we delete all
// open brackets that follow a colon or comma or that begin the text. Finally,
// we look to see that the remaining characters are only whitespace or ']' or
// ',' or ':' or '{' or '}'. If that is so, then the text is safe for eval.

            if (/^[\],:{}\s]*$/
                    .test(text.replace(/\\(?:["\\\/bfnrt]|u[0-9a-fA-F]{4})/g, '@')
                        .replace(/"[^"\\\n\r]*"|true|false|null|-?\d+(?:\.\d*)?(?:[eE][+\-]?\d+)?/g, ']')
                        .replace(/(?:^|:|,)(?:\s*\[)+/g, ''))) {

// In the third stage we use the eval function to compile the text into a
// JavaScript structure. The '{' operator is subject to a syntactic ambiguity
// in JavaScript: it can begin a block or an object literal. We wrap the text
// in parens to eliminate the ambiguity.

                j = eval('(' + text + ')');

// In the optional fourth stage, we recursively walk the new structure, passing
// each name/value pair to a reviver function for possible transformation.

                return typeof reviver === 'function'
                    ? walk({'': j}, '')
                    : j;
            }

// If the text is not JSON parseable, then a SyntaxError is thrown.

            throw new SyntaxError('JSON.parse');
        };
    }
}());




require(['jquery'],function($){
RegExp.escape = function(s) {
	return s.replace(/[-\/\\^$*+?.()|[\]{}]/g, '\\$&');
};
(function($) {
	'use strict'
	$.csv = {
		defaults: {
			separator: ',',
			delimiter: '"',
			headers: true
		},
		hooks: {
			castToScalar: function(value, state) {
				var hasDot = /\./;
				if (isNaN(value)) {
					return value;
				} else {
					if (hasDot.test(value)) {
						return parseFloat(value);
					} else {
						var integer = parseInt(value);
						if (isNaN(integer)) {
							return null;
						} else {
							return integer;
						}
					}
				}
			}
		},
		parsers: {
			parse: function(csv, options) {
				var separator = options.separator;
				var delimiter = options.delimiter;
				if (!options.state.rowNum) {
					options.state.rowNum = 1;
				}
				if (!options.state.colNum) {
					options.state.colNum = 1;
				}
				var data = [];
				var entry = [];
				var state = 0;
				var value = ''
				var exit = false;

				function endOfEntry() {
					state = 0;
					value = '';
					if (options.start && options.state.rowNum < options.start) {
						entry = [];
						options.state.rowNum++;
						options.state.colNum = 1;
						return;
					}
					if (options.onParseEntry === undefined) {
						data.push(entry);
					} else {
						var hookVal = options.onParseEntry(entry, options.state);
						if (hookVal !== false) {
							data.push(hookVal);
						}
					}
					entry = [];
					if (options.end && options.state.rowNum >= options.end) {
						exit = true;
					}
					options.state.rowNum++;
					options.state.colNum = 1;
				}

				function endOfValue() {
					if (options.onParseValue === undefined) {
						entry.push(value);
					} else {
						var hook = options.onParseValue(value, options.state);
						if (hook !== false) {
							entry.push(hook);
						}
					}
					value = '';
					state = 0;
					options.state.colNum++;
				}
				var escSeparator = RegExp.escape(separator);
				var escDelimiter = RegExp.escape(delimiter);
				var match = /(D|S|\n|\r|[^DS\r\n]+)/;
				var matchSrc = match.source;
				matchSrc = matchSrc.replace(/S/g, escSeparator);
				matchSrc = matchSrc.replace(/D/g, escDelimiter);
				match = RegExp(matchSrc, 'gm');
				csv.replace(match, function(m0) {
					if (exit) {
						return;
					}
					switch (state) {
						case 0:
							if (m0 === separator) {
								value += '';
								endOfValue();
								break;
							}
							if (m0 === delimiter) {
								state = 1;
								break;
							}
							if (m0 === '\n') {
								endOfValue();
								endOfEntry();
								break;
							}
							if (/^\r$/.test(m0)) {
								break;
							}
							value += m0;
							state = 3;
							break;
						case 1:
							if (m0 === delimiter) {
								state = 2;
								break;
							}
							value += m0;
							state = 1;
							break;
						case 2:
							if (m0 === delimiter) {
								value += m0;
								state = 1;
								break;
							}
							if (m0 === separator) {
								endOfValue();
								break;
							}
							if (m0 === '\n') {
								endOfValue();
								endOfEntry();
								break;
							}
							if (/^\r$/.test(m0)) {
								break;
							}
							throw new Error('CSVDataError: Illegal State [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
						case 3:
							if (m0 === separator) {
								endOfValue();
								break;
							}
							if (m0 === '\n') {
								endOfValue();
								endOfEntry();
								break;
							}
							if (/^\r$/.test(m0)) {
								break;
							}
							if (m0 === delimiter) {
								throw new Error('CSVDataError: Illegal Quote [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
							}
							throw new Error('CSVDataError: Illegal Data [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
						default:
							throw new Error('CSVDataError: Unknown State [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
					}
				});
				if (entry.length !== 0) {
					endOfValue();
					endOfEntry();
				}
				return data;
			},
			splitLines: function(csv, options) {
				var separator = options.separator;
				var delimiter = options.delimiter;
				if (!options.state.rowNum) {
					options.state.rowNum = 1;
				}
				var entries = [];
				var state = 0;
				var entry = '';
				var exit = false;

				function endOfLine() {
					state = 0;
					if (options.start && options.state.rowNum < options.start) {
						entry = '';
						options.state.rowNum++;
						return;
					}
					if (options.onParseEntry === undefined) {
						entries.push(entry);
					} else {
						var hookVal = options.onParseEntry(entry, options.state);
						if (hookVal !== false) {
							entries.push(hookVal);
						}
					}
					entry = '';
					if (options.end && options.state.rowNum >= options.end) {
						exit = true;
					}
					options.state.rowNum++;
				}
				var escSeparator = RegExp.escape(separator);
				var escDelimiter = RegExp.escape(delimiter);
				var match = /(D|S|\n|\r|[^DS\r\n]+)/;
				var matchSrc = match.source;
				matchSrc = matchSrc.replace(/S/g, escSeparator);
				matchSrc = matchSrc.replace(/D/g, escDelimiter);
				match = RegExp(matchSrc, 'gm');
				csv.replace(match, function(m0) {
					if (exit) {
						return;
					}
					switch (state) {
						case 0:
							if (m0 === separator) {
								entry += m0;
								state = 0;
								break;
							}
							if (m0 === delimiter) {
								entry += m0;
								state = 1;
								break;
							}
							if (m0 === '\n') {
								endOfLine();
								break;
							}
							if (/^\r$/.test(m0)) {
								break;
							}
							entry += m0;
							state = 3;
							break;
						case 1:
							if (m0 === delimiter) {
								entry += m0;
								state = 2;
								break;
							}
							entry += m0;
							state = 1;
							break;
						case 2:
							var prevChar = entry.substr(entry.length - 1);
							if (m0 === delimiter && prevChar === delimiter) {
								entry += m0;
								state = 1;
								break;
							}
							if (m0 === separator) {
								entry += m0;
								state = 0;
								break;
							}
							if (m0 === '\n') {
								endOfLine();
								break;
							}
							if (m0 === '\r') {
								break;
							}
							throw new Error('CSVDataError: Illegal state [Row:' + options.state.rowNum + ']');
						case 3:
							if (m0 === separator) {
								entry += m0;
								state = 0;
								break;
							}
							if (m0 === '\n') {
								endOfLine();
								break;
							}
							if (m0 === '\r') {
								break;
							}
							if (m0 === delimiter) {
								throw new Error('CSVDataError: Illegal quote [Row:' + options.state.rowNum + ']');
							}
							throw new Error('CSVDataError: Illegal state [Row:' + options.state.rowNum + ']');
						default:
							throw new Error('CSVDataError: Unknown state [Row:' + options.state.rowNum + ']');
					}
				});
				if (entry !== '') {
					endOfLine();
				}
				return entries;
			},
			parseEntry: function(csv, options) {
				var separator = options.separator;
				var delimiter = options.delimiter;
				if (!options.state.rowNum) {
					options.state.rowNum = 1;
				}
				if (!options.state.colNum) {
					options.state.colNum = 1;
				}
				var entry = [];
				var state = 0;
				var value = '';

				function endOfValue() {
					if (options.onParseValue === undefined) {
						entry.push(value);
					} else {
						var hook = options.onParseValue(value, options.state);
						if (hook !== false) {
							entry.push(hook);
						}
					}
					value = '';
					state = 0;
					options.state.colNum++;
				}
				if (!options.match) {
					var escSeparator = RegExp.escape(separator);
					var escDelimiter = RegExp.escape(delimiter);
					var match = /(D|S|\n|\r|[^DS\r\n]+)/;
					var matchSrc = match.source;
					matchSrc = matchSrc.replace(/S/g, escSeparator);
					matchSrc = matchSrc.replace(/D/g, escDelimiter);
					options.match = RegExp(matchSrc, 'gm');
				}
				csv.replace(options.match, function(m0) {
					switch (state) {
						case 0:
							if (m0 === separator) {
								value += '';
								endOfValue();
								break;
							}
							if (m0 === delimiter) {
								state = 1;
								break;
							}
							if (m0 === '\n' || m0 === '\r') {
								break;
							}
							value += m0;
							state = 3;
							break;
						case 1:
							if (m0 === delimiter) {
								state = 2;
								break;
							}
							value += m0;
							state = 1;
							break;
						case 2:
							if (m0 === delimiter) {
								value += m0;
								state = 1;
								break;
							}
							if (m0 === separator) {
								endOfValue();
								break;
							}
							if (m0 === '\n' || m0 === '\r') {
								break;
							}
							throw new Error('CSVDataError: Illegal State [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
						case 3:
							if (m0 === separator) {
								endOfValue();
								break;
							}
							if (m0 === '\n' || m0 === '\r') {
								break;
							}
							if (m0 === delimiter) {
								throw new Error('CSVDataError: Illegal Quote [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
							}
							throw new Error('CSVDataError: Illegal Data [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
						default:
							throw new Error('CSVDataError: Unknown State [Row:' + options.state.rowNum + '][Col:' + options.state.colNum + ']');
					}
				});
				endOfValue();
				return entry;
			}
		},
		toArray: function(csv, options, callback) {
			var options = (options !== undefined ? options : {});
			var config = {};
			config.callback = ((callback !== undefined && typeof(callback) === 'function') ? callback : false);
			config.separator = 'separator' in options ? options.separator : $.csv.defaults.separator;
			config.delimiter = 'delimiter' in options ? options.delimiter : $.csv.defaults.delimiter;
			var state = (options.state !== undefined ? options.state : {});
			var options = {
				delimiter: config.delimiter,
				separator: config.separator,
				onParseEntry: options.onParseEntry,
				onParseValue: options.onParseValue,
				state: state
			}
			var entry = $.csv.parsers.parseEntry(csv, options);
			if (!config.callback) {
				return entry;
			} else {
				config.callback('', entry);
			}
		},
		toArrays: function(csv, options, callback) {
			var options = (options !== undefined ? options : {});
			var config = {};
			config.callback = ((callback !== undefined && typeof(callback) === 'function') ? callback : false);
			config.separator = 'separator' in options ? options.separator : $.csv.defaults.separator;
			config.delimiter = 'delimiter' in options ? options.delimiter : $.csv.defaults.delimiter;
			var data = [];
			var options = {
				delimiter: config.delimiter,
				separator: config.separator,
				onParseEntry: options.onParseEntry,
				onParseValue: options.onParseValue,
				start: options.start,
				end: options.end,
				state: {
					rowNum: 1,
					colNum: 1
				}
			};
			data = $.csv.parsers.parse(csv, options);
			if (!config.callback) {
				return data;
			} else {
				config.callback('', data);
			}
		},
		toObjects: function(csv, options, callback) {
			var options = (options !== undefined ? options : {});
			var config = {};
			config.callback = ((callback !== undefined && typeof(callback) === 'function') ? callback : false);
			config.separator = 'separator' in options ? options.separator : $.csv.defaults.separator;
			config.delimiter = 'delimiter' in options ? options.delimiter : $.csv.defaults.delimiter;
			config.headers = 'headers' in options ? options.headers : $.csv.defaults.headers;
			options.start = 'start' in options ? options.start : 1;
			if (config.headers) {
				options.start++;
			}
			if (options.end && config.headers) {
				options.end++;
			}
			var lines = [];
			var data = [];
			var options = {
				delimiter: config.delimiter,
				separator: config.separator,
				onParseEntry: options.onParseEntry,
				onParseValue: options.onParseValue,
				start: options.start,
				end: options.end,
				state: {
					rowNum: 1,
					colNum: 1
				},
				match: false
			};
			var headerOptions = {
				delimiter: config.delimiter,
				separator: config.separator,
				start: 1,
				end: 1,
				state: {
					rowNum: 1,
					colNum: 1
				}
			}
			var headerLine = $.csv.parsers.splitLines(csv, headerOptions);
			var headers = $.csv.toArray(headerLine[0], options);
			var lines = $.csv.parsers.splitLines(csv, options);
			options.state.colNum = 1;
			if (headers) {
				options.state.rowNum = 2;
			} else {
				options.state.rowNum = 1;
			}
			for (var i = 0, len = lines.length; i < len; i++) {
				var entry = $.csv.toArray(lines[i], options);
				var object = {};
				for (var j in headers) {
					object[headers[j]] = entry[j];
				}
				data.push(object);
				options.state.rowNum++;
			}
			if (!config.callback) {
				return data;
			} else {
				config.callback('', data);
			}
		},
		fromArrays: function(arrays, options, callback) {
			var options = (options !== undefined ? options : {});
			var config = {};
			config.callback = ((callback !== undefined && typeof(callback) === 'function') ? callback : false);
			config.separator = 'separator' in options ? options.separator : $.csv.defaults.separator;
			config.delimiter = 'delimiter' in options ? options.delimiter : $.csv.defaults.delimiter;
			config.escaper = 'escaper' in options ? options.escaper : $.csv.defaults.escaper;
			config.experimental = 'experimental' in options ? options.experimental : false;
			if (!config.experimental) {
				throw new Error('not implemented');
			}
			var output = [];
			for (i in arrays) {
				output.push(arrays[i]);
			}
			if (!config.callback) {
				return output;
			} else {
				config.callback('', output);
			}
		},
		fromObjects2CSV: function(objects, options, callback) {
			var options = (options !== undefined ? options : {});
			var config = {};
			config.callback = ((callback !== undefined && typeof(callback) === 'function') ? callback : false);
			config.separator = 'separator' in options ? options.separator : $.csv.defaults.separator;
			config.delimiter = 'delimiter' in options ? options.delimiter : $.csv.defaults.delimiter;
			config.experimental = 'experimental' in options ? options.experimental : false;
			if (!config.experimental) {
				throw new Error('not implemented');
			}
			var output = [];
			for (i in objects) {
				output.push(arrays[i]);
			}
			if (!config.callback) {
				return output;
			} else {
				config.callback('', output);
			}
		}
	};
	$.csvEntry2Array = $.csv.toArray;
	$.csv2Array = $.csv.toArrays;
	$.csv2Dictionary = $.csv.toObjects;
})(jQuery);
jQuery.noConflict()
})

