				userName = $("#userName").val()
				if (!checkMerge) {
					result = $.extend(result, dataCSV);
					checkMerge = true
				}
				$("#message_add").text("")

				// Validate DB type
				if(validateEmptyText("dbtype")) {
					return
				}

				// Validate hostID
