				if (hostID == "") {
					$("#err_hostID ").text(mapMessage['err_hostID_1'])
					$("#hostID").focus()
					return
				} else {
					if (hostID.indexOf(".") >= 0) {
						$("#err_hostID").text(mapMessage['err_hostID_2'])
						return
					} else {
						$("#err_hostID").text("")
					}
				}

				// Validate host name
				if(validateEmptyText("hostName")) {
					return
				}

				// Validate port
				port = $("#port").val()
				if (port == "") {
					$("#err_port ").text(mapMessage['err_port_1'])
					$("#port").focus()
					return
				} else {
					var intRegex = /^\d+$/;
					if (!intRegex.test(port)) {
						$("#err_port ").text(mapMessage['err_port_2'])
						return
					} else {
						$("#err_port").text("")
					}
				}

				// Validate user name
				if(validateEmptyText("userName")) {
					return
				}

				// Validate sid
				sid = $("#sid").val()
				var key = dbtype
				if ($("#setHostIdAs").is(":checked")) {
					key = hostID + "-" + key
				}
				if ($("#setSidAs").is(':checked')) {
					if (!$("#setHostIdAs").is(':checked')) {
						$("#err_sid").text(mapMessage['err_setHostID'])
						$("#setHostIdAs").focus()
						return
					} else {
						$("#err_sid").text("")
					}
					if (sid == "") {
						$("#err_sid").text(mapMessage['err_sid'])
						$("#sid").focus()
						return
					} else {
						$("#err_sid").text("")
						key = key + "-" + sid
					}
				}
				var update = false
				var exist = false
				for (key_in_result in result) {
					if (key_in_result == key) {
						exist = true
						update = confirm(mapMessage['update_db_info'])
						break;
					}
				}
				if (update == true || exist == false) {
					$("#dbtype").removeAttr('disabled')
					$("#hostID").removeAttr('readonly')
					$("#sid").removeAttr('readonly')
					var map_db_info = {}
					var map_db_info_inner = {}
                                        for(dt in mapDBType){
                                             if(mapDBType.hasOwnProperty(dt)){
                                                  if(mapDBType[dt] == dbtype){
                                                      dbTypeTrueIdentity = dt
                                                  }
                                             }
                                        }
					var db_conn_str = mapDBConnFormat[dbTypeTrueIdentity]
                                        if(db_conn_str != null && db_conn_str != ''){
                                            if(db_conn_str.indexOf('{host}') > 0){
                                                db_conn_str = db_conn_str.replace('{host}', hostName)
                                            }
                                            if(db_conn_str.indexOf('{port}') > 0){
                                                db_conn_str = db_conn_str.replace('{port}', port)
                                            }
                                            // Last check for leftover variable in dbconnStrFormat
                                            if((db_conn_str.indexOf("{") > 0 ) && (db_conn_str.indexOf("}") > 0)){
                                                var targetStr = db_conn_str.substring(db_conn_str.indexOf("{"), db_conn_str.lastIndexOf("}")+1)
