                                            }
                                        }else{
                                            db_conn_str = window.prompt("Look like Wiperdog configuration for " + dbTypeTrueIdentity + " is null or empty.\n Please insert database connection string manually for it or config Wiperdog.\nIt should be something like jdbc://{host}:{port}/{something_else}","jdbc:" + dbTypeTrueIdentity + "://host:port");
                                        }
                                        if(db_conn_str != null && db_conn_str != ''){
    					    map_db_info_inner["dbconnstr"] = db_conn_str
  					    map_db_info_inner["user"] = userName
