				if(arrayAll != null && arrayAll != []) {
					//get data area respective and parse to Json 
					arrayContainerAll = jQuery.parseJSON($("#jsons>.lstcontainerarea").val());
					var lstContainer = [];
					var data_all = [];
					//get size of data area
					var chartSize = arrayAll.length
					//for each data of data area
					for(var i=0;i<chartSize ;i++){
						//set data for draw chart
						data_all = arrayAll[i]
						//set list container
						lstContainer = arrayContainerAll[i]
