def prepareContainerArea(jsonArea, chartColumnsArea, fromDateFinal, toDateFinal, typeChart, key) {
	if(jsonArea != null) {
		def chartAreaComment = false
		def chartNumArea = 0
		//for each data of list chart columns
		chartColumnsArea.each{chartColumnsAreaItem ->
			//show information of chart
			if(!chartAreaComment){
		        println "<div id = 'areaChart'  style = 'clear:both'><span class = 'chartDesc'>Area chart : This chart is monitoring data " + fromDateFinal + toDateFinal + "&nbsp;</span><span class = 'showHideLink'><a class='toggleArea' id = 'show'>Hide area charts  </a></span>"
		        println "&nbsp;"
				println "<div id=\"wrapperAreaChart\" style=\"clear:both\">"
				chartAreaComment = true
			}
