			if(typeChart == "Subtyped") {
				chartColumnsAreaItem.each{ println " <div class=\"displayBlockArea\" id=\"containerarea${key}_${chartNumArea}_${it}\"></div>" }
			} else if (typeChart == "Store") {
				chartColumnsAreaItem.each{ println " <div class=\"displayBlockArea\" id=\"containerarea${chartNumArea}_${it}\"></div>" }
			}
			chartNumArea++
		}
		if(chartAreaComment){
			println "</div>"
