			var arrayAll
			var arrayContainerAll
			//check data Subtype or Store
			if(lstKey.length > 0) {
				//data Subtype
				for(var eKey=0;eKey<lstKey.length;eKey++){
					var finalKey = lstKey[eKey]
					//for each key of list Key => check data json line
