			if(typeChart == "Subtyped") {
				chartColumnsAreaItem.each{ println " <div class=\"displayblock\" id=\"containerarea${key}_${chartNumArea}_${it}\"></div>" }
			} else if (typeChart == "Store") {
				chartColumnsAreaItem.each{ println " <div class=\"displayblock\" id=\"containerarea${chartNumArea}_${it}\"></div>" }
			}
			chartNumArea++
		}
