  try{
    FileWriter fw = new FileWriter(f)
    BufferedWriter bw = new BufferedWriter(fw);
    bw.write(data.replaceAll("\\{","\\[").replaceAll("\\}","\\]").replaceAll("\\\\", "\\\\\\\\"));
    bw.close();
    message = "Create infomartion succesfully !"
   }catch(Exception ex){
    message = "Failed to create informartion !"

   }


}


