if (!Suggest) {
  var Suggest = {};
}
/*-- KeyCodes -----------------------------------------*/
Suggest.Key = {
  TAB:     9,
  RETURN: 13,
  ESC:    27,
  UP:     38,
  DOWN:   40
};

/*-- Utils --------------------------------------------*/
Suggest.copyProperties = function(dest, src) {
  for (var property in src) {
    dest[property] = src[property];
  }
  return dest;
};

/*-- Suggest.Local ------------------------------------*/
Suggest.Local = function() {
  this.initialize.apply(this, arguments);
};
Suggest.Local.prototype = {
  initialize: function(input, suggestArea, candidateList) {

    this.input = this._getElement(input);
    this.suggestArea = this._getElement(suggestArea);
    this.candidateList = candidateList;
    this.oldText = this.getInputText();

    if (arguments[3]) this.setOptions(arguments[3]);

    // reg event
    this._addEvent(this.input, 'focus', this._bind(this.checkLoop));
    this._addEvent(this.input, 'blur', this._bind(this.inputBlur));
    this._addEvent(this.suggestArea, 'blur', this._bind(this.inputBlur));

    this._addEvent(this.input, 'keydown', this._bindEvent(this.keyEvent));

    // init
    this.clearSuggestArea();
  },

  // options
  interval: 500,
  dispMax: 20,
  listTagName: 'div',
  prefix: false,
  ignoreCase: true,
  highlight: false,
  dispAllKey: false,
  classMouseOver: 'over',
  classSelect: 'select',
  hookBeforeSearch: function(){},

  setOptions: function(options) {
    Suggest.copyProperties(this, options);
  },

  inputBlur: function() {

    setTimeout(this._bind(function(){

      if (document.activeElement == this.suggestArea
          || document.activeElement == this.input) {
        // keep suggestion
        return;
      }

      this.changeUnactive();
      this.oldText = this.getInputText();

      if (this.timerId) clearTimeout(this.timerId);
      this.timerId = null;

      setTimeout(this._bind(this.clearSuggestArea), 500);
    }, 500));
  },

  checkLoop: function() {
    var text = this.getInputText();
    if (text != this.oldText) {
      this.oldText = text;
      this.search();
    }
    if (this.timerId) clearTimeout(this.timerId);
    this.timerId = setTimeout(this._bind(this.checkLoop), this.interval);
  },

  search: function() {

    // init
    this.clearSuggestArea();

    var text = this.getInputText();

    if (text == '' || text == null) return;

    this.hookBeforeSearch(text);
    var resultList = this._search(text);
    if (resultList.length != 0) this.createSuggestArea(resultList);
  },

  _search: function(text) {

    var resultList = [];
    var temp; 
    this.suggestIndexList = [];

    for (var i = 0, length = this.candidateList.length; i < length; i++) {
      if ((temp = this.isMatch(this.candidateList[i], text)) != null) {
        resultList.push(temp);
        this.suggestIndexList.push(i);

        if (this.dispMax != 0 && resultList.length >= this.dispMax) break;
      }
    }
    return resultList;
  },

  isMatch: function(value, pattern) {

    if (value == null) return null;

    var pos = (this.ignoreCase) ?
      value.toLowerCase().indexOf(pattern.toLowerCase())
      : value.indexOf(pattern);

    if ((pos == -1) || (this.prefix && pos != 0)) return null;

    if (this.highlight) {
      return (this._escapeHTML(value.substr(0, pos)) + '<strong>' 
             + this._escapeHTML(value.substr(pos, pattern.length)) 
               + '</strong>' + this._escapeHTML(value.substr(pos + pattern.length)));
    } else {
      return this._escapeHTML(value);
    }
  },

  clearSuggestArea: function() {
    this.suggestArea.innerHTML = '';
    this.suggestArea.style.display = 'none';
    this.suggestList = null;
    this.suggestIndexList = null;
    this.activePosition = null;
  },

  createSuggestArea: function(resultList) {

    this.suggestList = [];
    this.inputValueBackup = this.input.value;

    for (var i = 0, length = resultList.length; i < length; i++) {
      var element = document.createElement(this.listTagName);
      element.innerHTML = resultList[i];
      this.suggestArea.appendChild(element);

      this._addEvent(element, 'click', this._bindEvent(this.listClick, i));
      this._addEvent(element, 'mouseover', this._bindEvent(this.listMouseOver, i));
      this._addEvent(element, 'mouseout', this._bindEvent(this.listMouseOut, i));

      this.suggestList.push(element);
    }

    this.suggestArea.style.display = '';
    this.suggestArea.scrollTop = 0;
  },

  getInputText: function() {
    return this.input.value;
  },

  setInputText: function(text) {
    this.input.value = text;
  },

  // key event
  keyEvent: function(event) {
    if (!this.timerId) {
      this.timerId = setTimeout(this._bind(this.checkLoop), this.interval);
    }

    if (this.dispAllKey && event.ctrlKey 
        && this.getInputText() == ''
        && !this.suggestList
        && event.keyCode == Suggest.Key.DOWN) {
      // dispAll
      this._stopEvent(event);
      this.keyEventDispAll();
    } else if (event.keyCode == Suggest.Key.UP ||
               event.keyCode == Suggest.Key.DOWN) {
      // key move
      if (this.suggestList && this.suggestList.length != 0) {
        this._stopEvent(event);
        this.keyEventMove(event.keyCode);
      }
    } else if (event.keyCode == Suggest.Key.RETURN) {
      // fix
      if (this.suggestList && this.suggestList.length != 0) {
        console.log(event)
        this._stopEvent(event);
        this.keyEventReturn();
      }
    } else if (event.keyCode == Suggest.Key.ESC) {
      // cancel
      if (this.suggestList && this.suggestList.length != 0) {
        this._stopEvent(event);
        this.keyEventEsc();
      }
    } else {
      this.keyEventOther(event);
    }
  },

  keyEventDispAll: function() {

    // init
    this.clearSuggestArea();

    this.oldText = this.getInputText();

    this.suggestIndexList = [];
    for (var i = 0, length = this.candidateList.length; i < length; i++) {
      this.suggestIndexList.push(i);
    }

    this.createSuggestArea(this.candidateList);
  },

  keyEventMove: function(keyCode) {

    this.changeUnactive();

    if (keyCode == Suggest.Key.UP) {
      // up
      if (this.activePosition == null) {
        this.activePosition = this.suggestList.length -1;
      }else{
        this.activePosition--;
        if (this.activePosition < 0) {
          this.activePosition = null;
          this.input.value = this.inputValueBackup;
          this.suggestArea.scrollTop = 0;
          return;
        }
      }
    }else{
      // down
      if (this.activePosition == null) {
        this.activePosition = 0;
      }else{
        this.activePosition++;
      }

      if (this.activePosition >= this.suggestList.length) {
        this.activePosition = null;
        this.input.value = this.inputValueBackup;
        this.suggestArea.scrollTop = 0;
        return;
      }
    }

    this.changeActive(this.activePosition);
  },

  keyEventReturn: function() {

    this.clearSuggestArea();
    this.moveEnd();
  },

  keyEventEsc: function() {

    this.clearSuggestArea();
    this.input.value = this.inputValueBackup;
    this.oldText = this.getInputText();

    if (window.opera) setTimeout(this._bind(this.moveEnd), 5);
  },

  keyEventOther: function(event) {},

  changeActive: function(index) {

    this.setStyleActive(this.suggestList[index]);

    this.setInputText(this.candidateList[this.suggestIndexList[index]]);

    this.oldText = this.getInputText();
    this.input.focus();
  },

  changeUnactive: function() {

    if (this.suggestList != null 
        && this.suggestList.length > 0
        && this.activePosition != null) {
      this.setStyleUnactive(this.suggestList[this.activePosition]);
    }
  },

  listClick: function(event, index) {

    this.changeUnactive();
    this.activePosition = index;
    this.changeActive(index);

    this.clearSuggestArea();
    this.moveEnd();
  },

  listMouseOver: function(event, index) {
    this.setStyleMouseOver(this._getEventElement(event));
  },

  listMouseOut: function(event, index) {

    if (!this.suggestList) return;

    var element = this._getEventElement(event);

    if (index == this.activePosition) {
      this.setStyleActive(element);
    }else{
      this.setStyleUnactive(element);
    }
  },

  setStyleActive: function(element) {
    element.className = this.classSelect;

    // auto scroll
    var offset = element.offsetTop;
    var offsetWithHeight = offset + element.clientHeight;

    if (this.suggestArea.scrollTop > offset) {
      this.suggestArea.scrollTop = offset
    } else if (this.suggestArea.scrollTop + this.suggestArea.clientHeight < offsetWithHeight) {
      this.suggestArea.scrollTop = offsetWithHeight - this.suggestArea.clientHeight;
    }
  },

  setStyleUnactive: function(element) {
    element.className = '';
  },

  setStyleMouseOver: function(element) {
    element.className = this.classMouseOver;
  },

  moveEnd: function() {
    if (this.input.createTextRange) {
      this.input.focus(); // Opera
      var range = this.input.createTextRange();
      range.move('character', this.input.value.length);
      range.select();
    } else if (this.input.setSelectionRange) {
      this.input.setSelectionRange(this.input.value.length, this.input.value.length);
    }
  },

  // Utils
  _getElement: function(element) {
    return (typeof element == 'string') ? document.getElementById(element) : element;
  },
  _addEvent: (window.addEventListener ?
    function(element, type, func) {
      element.addEventListener(type, func, false);
    } :
    function(element, type, func) {
      element.attachEvent('on' + type, func);
    }),
  _stopEvent: function(event) {
    if (event.preventDefault) {
      event.preventDefault();
      event.stopPropagation();
    } else {
      event.returnValue = false;
      event.cancelBubble = true;
    }
  },
  _getEventElement: function(event) {
    return event.target || event.srcElement;
  },
  _bind: function(func) {
    var self = this;
    var args = Array.prototype.slice.call(arguments, 1);
    return function(){ func.apply(self, args); };
  },
  _bindEvent: function(func) {
    var self = this;
    var args = Array.prototype.slice.call(arguments, 1);
    return function(event){ event = event || window.event; func.apply(self, [event].concat(args)); };
  },
  _escapeHTML: function(value) {
    return value.replace(/\&/g, '&amp;').replace( /</g, '&lt;').replace(/>/g, '&gt;')
             .replace(/\"/g, '&quot;').replace(/\'/g, '&#39;');
  }
};

/*-- Suggest.LocalMulti ---------------------------------*/
Suggest.LocalMulti = function() {
  this.initialize.apply(this, arguments);
};
Suggest.copyProperties(Suggest.LocalMulti.prototype, Suggest.Local.prototype);

Suggest.LocalMulti.prototype.delim = ' '; // delimiter

Suggest.LocalMulti.prototype.keyEventReturn = function() {

  this.clearSuggestArea();
  this.input.value += this.delim;
  this.moveEnd();
};

Suggest.LocalMulti.prototype.keyEventOther = function(event) {

  if (event.keyCode == Suggest.Key.TAB) {
    // fix
    if (this.suggestList && this.suggestList.length != 0) {
      this._stopEvent(event);

      if (!this.activePosition) {
        this.activePosition = 0;
        this.changeActive(this.activePosition);
      }

      this.clearSuggestArea();
      this.input.value += this.delim;
      if (window.opera) {
        setTimeout(this._bind(this.moveEnd), 5);
      } else {
        this.moveEnd();
      }
    }
  }
};

Suggest.LocalMulti.prototype.listClick = function(event, index) {

  this.changeUnactive();
  this.activePosition = index;
  this.changeActive(index);

  this.input.value += this.delim;

  this.clearSuggestArea();
  this.moveEnd();
};

Suggest.LocalMulti.prototype.getInputText = function() {

  var pos = this.getLastTokenPos();

  if (pos == -1) {
    return this.input.value;
  } else {
    return this.input.value.substr(pos + 1);
  }
};

Suggest.LocalMulti.prototype.setInputText = function(text) {

  var pos = this.getLastTokenPos();

  if (pos == -1) {
    this.input.value = text;
  } else {
    this.input.value = this.input.value.substr(0 , pos + 1) + text;
  }
};

Suggest.LocalMulti.prototype.getLastTokenPos = function() {
  return this.input.value.lastIndexOf(this.delim);
};

require(['jquery'], function($) {
  'use strict';

  var caretClass   = 'textarea-helper-caret'
    , dataKey      = 'textarea-helper'

    // Styles that could influence size of the mirrored element.
    , mirrorStyles = [ 
                       // Box Styles.
                       'box-sizing', 'height', 'width', 'padding-bottom'
                     , 'padding-left', 'padding-right', 'padding-top'

                       // Font stuff.
                     , 'font-family', 'font-size', 'font-style' 
                     , 'font-variant', 'font-weight'

                       // Spacing etc.
                     , 'word-spacing', 'letter-spacing', 'line-height'
                     , 'text-decoration', 'text-indent', 'text-transform' 

                      // The direction.
                     , 'direction'
                     ];

  var TextareaHelper = function (elem) {
    if (elem.nodeName.toLowerCase() !== 'textarea') return;
    this.$text = $(elem);
    this.$mirror = $('<div/>').css({ 'position'    : 'absolute'
                                   , 'overflow'    : 'auto'
                                   , 'white-space' : 'pre-wrap'
                                   , 'word-wrap'   : 'break-word'
                                   , 'top'         : 0
                                   , 'left'        : -9999
                                   }).insertAfter(this.$text);
  };

  (function () {
    this.update = function () {

      // Copy styles.
      var styles = {};
      for (var i = 0, style; style = mirrorStyles[i]; i++) {
        styles[style] = this.$text.css(style);
      }
      this.$mirror.css(styles).empty();
      var anchorPos= $("#anchor").position()
      // Update content and insert caret.
      var caretPos = this.getOriginalCaretPos()
        , str      = this.$text.val()
        , pre      = document.createTextNode(str.substring(0, caretPos))
        , post     = document.createTextNode(str.substring(caretPos))
        , $car     = $('<span/>').addClass(caretClass).css('position', 'absolute').html('&nbsp;');
      this.$mirror.append(pre, $car, post)
                  .scrollTop(this.$text.scrollTop())

    };

    this.destroy = function () {
      this.$mirror.remove();
      this.$text.removeData(dataKey);
      return null;
    };

    this.caretPos = function () {
      this.update();
      var $caret = this.$mirror.find('.' + caretClass)
        , pos    = $caret.position();
      if (this.$text.css('direction') === 'rtl') {
        pos.right = this.$mirror.innerWidth() - pos.left - $caret.width();
        pos.left = 'auto';
      }
      var anchorPos= $("#anchor").position()
      pos['margin-left'] = anchorPos.left
      pos['top'] = anchorPos.top
      return pos;
    };

    this.height = function () {
      this.update();
      this.$mirror.css('height', '');
      return this.$mirror.height();
    };

    // XBrowser caret position
    // Adapted from http://stackoverflow.com/questions/263743/how-to-get-caret-position-in-textarea
    this.getOriginalCaretPos = function () {
      var text = this.$text[0];
      if (text.selectionStart) {
        return text.selectionStart;
      } else if (document.selection) {
        text.focus();
        var r = document.selection.createRange();
        if (r == null) {
          return 0;
        }
        var re = text.createTextRange()
          , rc = re.duplicate();
        re.moveToBookmark(r.getBookmark());
        rc.setEndPoint('EndToStart', re);
        return rc.text.length;
      } 
      return 0;
    };

  }).call(TextareaHelper.prototype);

  $.fn.textareaHelper = function (method) {
    this.each(function () {
      var $this    = $(this)
        , instance = $this.data(dataKey);
      if (!instance) {
        instance = new TextareaHelper(this);
        $this.data(dataKey, instance);
      }
    });
    if (method) {
      var instance = this.first().data(dataKey);
      return instance[method]();
    } else {
      return this;
    }
  };


    jQuery.noConflict()
});

var servletName
var listPolicy = new Array()
var mapPolicy = {}
var tmpPolicyForKey = {}
var mapPolicySubtyped = {}
var currentIstIidSelector
var listKey
var maxRow = 0
var mapNameAndType

// SUGGEST FUNCTION
function startSuggest(list) {
    new Suggest.LocalMulti(
        "inputText",    // input element id.
        "suggest", // suggestion area id.
        list,      // suggest candidates list
        {dispMax: 10, interval: 500}
    ); // options
}

// STRING REPLACE
function replaceAllString(oriStr, strTarget, strSubString ){
   var strText = oriStr;
   var intIndexOfMatch = strText.indexOf( strTarget );
   while (intIndexOfMatch != -1){
      strText = strText.replace( strTarget, strSubString )
      intIndexOfMatch = strText.indexOf( strTarget );
   }
   return( strText );
}

// RESET TO INITIAL SCREEN
function resetControl() {
    require(['jquery'], function($) {
        $("#inputText").val("")
        $("#messageText").val("")
        $("#msgValidate").empty()
        $("#sampleData").find("tr:gt(0)").remove();
        $("#policyInfo").find("tr:gt(0)").remove();
        $("#previewText").val("")
        $("#runTestText").val("")
        $("#inputText").prop('disabled', false)
        $("#policyIdText").val("")
        $("#groupSubtyped").css('display','none')
        $("#groupSubtyped").html("")
        $('input:radio[name="level"][value="Low"]').prop('checked', true);
        mapPolicy = {}
        tmpPolicyForKey = {}
        mapPolicySubtyped = {}
        maxRow = 0
    jQuery.noConflict()
    });
}

// DRAW SAMPLE DATA TABLE AND POLICY DATA TABLE
function drawTable(istIid){
    require(['jquery'], function($) {
        var selectedIstIid = istIid.options[istIid.selectedIndex].value
        currentIstIidSelector = selectedIstIid
        resetControl()
        var jobName = $("#listJob option:selected").val()
        if(selectedIstIid != "noChoice") {
            jobName += "." + selectedIstIid
        }
        processChooseJob(jobName)
        var responseData
        var typeOfData
        for(var key in mapNameAndType) {
            if(key == jobName || key.indexOf(jobName) != -1) {
                typeOfData = mapNameAndType[key]
            }
        }
        $.ajax({
            type: "GET",
            crossDomain: true,
            url: servletName + "?job_name=" + jobName + "&type=" + typeOfData,
            dataType: 'json',
            success: function(result) {
                responseData = result
                if(typeOfData == "Store") {
                    var policyData = responseData.POLICY.listpolicy

                    // FILL POLICY INFORMATIO INTO TABLE POLICY
                    var htmlPolicyInfoSource = '<tr><th>PolicyID</th><th>Condition</th><th>Message</th><th>Level</th><th style="width:125px;min-width:125px">Action</th></tr>'
                    if (policyData != null) {
                        for (var i = 0; i < policyData.length; i++) {
                            var mapElement = policyData[i]
                            mapPolicy[mapElement.condition] = mapElement.message
                            maxRow = i + 1
                            htmlPolicyInfoSource += '<tr><td>' + maxRow + '</td><td>' + mapElement.condition + '</td><td>'+ mapElement.message +'</td><td class="' + getLevelStr(mapElement.level) + '">'+ getLevelStr(mapElement.level) +'</td><td><input type="button" class="delete_btn button" value="delete"/><input type="button" class="edit_btn button" value="edit"/></td></tr>'
                        };
                    };
                    $("#policyInfo").html(htmlPolicyInfoSource)
                } else if(typeOfData == "Subtyped") {
                    maxRow = 0
                    var htmlPolicyInfoSource = '<tr><th>PolicyID</th><th>Group</th><th>Condition</th><th>Message</th><th>Level</th><th style="width:125px;min-width:125px">Action</th></tr>'
                    for(var key in responseData.POLICY.listpolicy) {
                        var policyData = responseData.POLICY.listpolicy[key]
                        if (policyData != null) {
                            for (var i = 0; i < policyData.length; i++) {
                                var mapElement = policyData[i]
                                tmpPolicyForKey[mapElement.condition] = mapElement.message
                                maxRow = maxRow + 1
                                htmlPolicyInfoSource += '<tr><td>' + maxRow + '</td><td>' + key + '</td><td>' + mapElement.condition + '</td><td>'+ mapElement.message +'</td><td class="' + getLevelStr(mapElement.level) + '">'+ getLevelStr(mapElement.level) +'</td><td><input type="button" class="delete_btn button" value="delete"/><input type="button" class="edit_btn button" value="edit"/></td></tr>'
                            };
                        };
                        mapPolicySubtyped[key] = tmpPolicyForKey
                    }
                    $("#policyInfo").html(htmlPolicyInfoSource)
                }
            },
            error: function(xhr, status, error) {
                console.log(error)
                console.log(status)
                console.log(xhr)
            }
        });
    jQuery.noConflict()
    });
}

// GET LIST JOB, DATA SAMPLE OF JOB IN MONGODB FROM PAGE CHOOSEJOBPROCESS
function processChooseJob(jobName) {
    require(['jquery'], function($) {
        var data2send
        if(jobName == null || jobName == "") {
            data2send = {'jobName': ''}
            $.ajax({
                type: "POST",
                crossDomain: true,
                url: "/xwiki/bin/get/WiperDogLib/chooseJobProcess?outputSyntax=plain",
                dataType: 'text',
                data: data2send,
                async: false,
                success: function(result) {
                    $("#initLoading").css("display","none")
                    mapNameAndType = $.parseJSON(result)
                    var lstJobName = new Array()
                    for(var key in mapNameAndType) {
                        lstJobName.push(key)
                    }
                    generateIstIidAndJobList(lstJobName)
                },
                error: function(xhr, status, error) {
                    alert("There is an error! Wiperdog agent is on yet?")
                    console.log(error)
                    console.log(status)
                    console.log(xhr)
                }
            });
        } else {
            data2send = {'jobName': jobName}
            $("#loadingImg").css("display","block")
            $.ajax({
                type: "POST",
                crossDomain: true,
                url: "/xwiki/bin/get/WiperDogLib/chooseJobProcess?outputSyntax=plain",
                dataType: 'text',
                data: data2send,
                //async: false,
                success: function(result) {
                    result = JSON.parse(result)
                    responseData = result
                    var sampleData = responseData.SAMPLE
                    var mapUnit = responseData.UNIT
                    var type = responseData.TYPE

                    if(type == "Store") {
                        // CREATE CONDITION BY SUGGEST
                        listKey = new Array()
                        for(var key in sampleData) {
                            if(key != "RECORD_SEQ") {
                                listKey.push(key)
                            }
                        }
                        startSuggest(listKey)

                        // FILL MAPUNIT INTO JOB'S DATA
                        var htmlJobDataSource = "<tr><th>Field</th><th>Unit</th><th>Data Sample</th></tr>"
                        if(sampleData != null) {
                            for(var key in sampleData) {
                                if(key != "RECORD_SEQ") {
                                    htmlJobDataSource += "<tr><td>" + key + "</td><td>" + mapUnit[key] +"</td><td>" + sampleData[key] +"</td></tr>"
                                }
                            }
                        };
                        $("#sampleData").html(htmlJobDataSource)
                        $("#loadingImg").css("display","none")
                    } else if(type == "Subtyped") {
                        var  htmlGroupSource = "<select id=\"selectBoxId\"><option id=\"noChoice\" value=\"noChoice\">-select a group-</option>"
                        for(var key in sampleData) {
                            htmlGroupSource += "<option id=\"group_" + key + "\" value= \"" + key + "\">" + key + "</option>"
                        }
                        htmlGroupSource += "</select> <b style=\"color:red\">(*)</b>"
                        $("#groupSubtyped").html(htmlGroupSource)
                        listKey = new Array()
                        var htmlJobDataSource = "<tr><th>Group</th><th>Field</th><th>Unit</th><th>Data Sample</th></tr>"
                        for(var keyGroup in sampleData) {
                            var mapSampleData = sampleData[keyGroup]
                            var mapUnitData = mapUnit[keyGroup]

                            // CREATE CONDITION BY SUGGEST
                            for(var key in mapSampleData) {
                                if(key != "RECORD_SEQ" && listKey.indexOf(key) == -1) {
                                    listKey.push(key)
                                }
                            }

                            for(var key in mapSampleData) {
                                if(key != "RECORD_SEQ") {
                                    htmlJobDataSource += "<tr><td>" + keyGroup +  "</td><td>" + key + "</td><td>" + mapUnitData[key] +"</td><td>" + mapSampleData[key] +"</td></tr>"
                                }
                            }
                        }
                        startSuggest(listKey)
                        $("#sampleData").html(htmlJobDataSource)
                        // HIDE LOADING IMG
                        $("#loadingImg").css("display","none")

                        // DISPLAY BLOCK SELECT BOX GROUPKEY
                        $("#groupSubtyped").css('display','block')
                    }
                },
                error: function(xhr, status, error) {
                    console.log(error)
                    console.log(status)
                    console.log(xhr)
                    $("#loadingImg").css("display","none")
                }
            });

        }
    });
    jQuery.noConflict()
}

// GENERATE JOB AND INSTANCES INTO SELECT BOX
function generateIstIidAndJobList(result){
    require(['jquery'], function($) {
        var mapIstIid = {}
        for(var i = 0; i<= result.length - 1; i++){
            var collectionName =  result[i]
            var jobName = collectionName.substr(0, collectionName.lastIndexOf("."))
            if(mapIstIid[jobName] == null){
                mapIstIid[jobName] = []
            }
            var istIid = collectionName.substr(collectionName.lastIndexOf(".") + 1, collectionName.length)
            mapIstIid[jobName].push(istIid)
        }
        for(key in mapIstIid){
            $("#listJob").append("<option id=" + key + " value=" + key  + ">" + key + "</option>")
            var istIidSelector = "istIid_" + replaceAllString(key,".","_")
            $("#istIidArea").append("<select style=\"width:500px;display:none\" id=\"" + istIidSelector + "\" onchange=\"drawTable(" + istIidSelector + ")\">\n</select>")
            $("#" + istIidSelector).append("<option id=\"noChoice\" value=\"noChoice\">-select a istIid-</option>")
            var listIstIid = mapIstIid[key]
            for(var i = 0; i <= listIstIid.length - 1; i++){
                var istiid = listIstIid[i]
                $("#" + istIidSelector).append("<option id=\"" + istiid + "\" value=\"" + istiid + "\">" + istiid + "</option>")
            }
        }
    });
    jQuery.noConflict()
}

function getLevelStr(levelInt){
    var levelRet
    if(levelInt == 1){
        levelRet = 'Low'
    }
    if(levelInt == 2){
        levelRet = 'Medium'
    }
    if(levelInt == 3){
        levelRet = 'High'
    }
    return levelRet
}

