    }
    gmongoObject.close()
} catch(Exception ex) {
    def mapErrorMsg = ['status': 'fail', 'message': ex.message]
    builder = new JsonBuilder(mapErrorMsg)
    println builder.toString()

    mapErrorMsg = null
