{{groovy}}
import groovy.json.*
import org.wiperdog.consoleservice.ConsoleServiceLib

def csLib = new ConsoleServiceLib()

try{
	def cmd = request.getParameter("cmd");
	if(cmd != null && cmd != ""){
		def params
		def osName = System.getProperty('os.name')
		if(osName != null && osName.toLowerCase().indexOf('win') ==-1){
			def wiperdog_path = '/home/wiperdog'
			Properties props = System.getProperties();
			def xwiki_dir = props.getProperty('user.dir');
			def decidedWiperdog_path
			File f = new File(xwiki_dir +"/webapps/xwiki/resources/conf.params")
			if(f.exists()){
				def shell = new GroovyShell()
				params = shell.evaluate(f)
				if(params['wiperdog_path'] != null){
					decidedWiperdog_path = params['wiperdog_path']['path']
				}else{
					decidedWiperdog_path = wiperdog_path
				}
			}else{
				decidedWiperdog_path = wiperdog_path
			}

			if(request.xpage == "plain") {
				response.setContentType('text/html')
			}
			def errorStr = ""
			def command = ""
			if(params['wiperdog_path']['host'] != "localhost"){
			   command = "ssh -o PasswordAuthentication=no " + params['wiperdog_path']['user'].trim() + "@" + params['wiperdog_path']['host'].trim() + " "

			}
			def proc = null
			if(cmd != null && !cmd.equals("") && cmd.equals("start")){
				command += '/bin/sh ' + decidedWiperdog_path + '/bin/wiperdog start '
				proc = command.execute()
				proc.waitFor()
				errorStr = proc.err.text
				if(errorStr != null && errorStr  != '')
					println "<p> Information: ${errorStr}</p>"
				println "<p> Output: ${proc.in.text}</p>"
			}else if(cmd != null && !cmd.equals("") && cmd.equals("stop")){
				command += '/bin/sh ' + decidedWiperdog_path + '/bin/wiperdog stop '
				proc = command.execute()
				proc.waitFor()
				errorStr = proc.err.text
				if(errorStr != null && errorStr  != '')
				println "<p> Information: ${errorStr}</p>"
				println "<p> Output: ${proc.in.text}</p>"
			}else if(cmd != null && !cmd.equals("") && cmd.equals("status")){
				command += '/bin/sh ' + decidedWiperdog_path + '/bin/wiperdog status '
				proc = command.execute()
				proc.waitFor()
				errorStr = proc.err.text
				if(errorStr != null && errorStr  != '')
					println "<p> Information: ${errorStr}</p>"
				println "<p> Checking status: ${proc.in.text}</p>"
				def paramObj = new GetServlet()
				def consoleServlet = paramObj.getParam('console')
				command = 'curl -Is  ' + consoleServlet
				proc = command.execute()
				proc.waitFor()
				errorStr = proc.err.text
				if(errorStr != null && errorStr  != '')
				println "<p> Information: ${errorStr}</p>"
				println "<p> Checking echo servlet: ${proc.in.text}</p>"
			}else if(cmd != null && !cmd.equals("") && cmd.equals("hostinfo")){
				//Get host name
				def hostInfo = [:]
				def commandHostname = command + 'hostname '
				proc = commandHostname.execute()
				proc.waitFor()
				hostInfo['hostName'] = proc.in.text
				//Get IP
				def commandIP = command + '/sbin/ifconfig eth0 '
				proc = commandIP.execute()
				proc.waitFor()
				def tmpHostIp = proc.in.text
				def hostIp = ''

				if(tmpHostIp != null && tmpHostIp.indexOf("inet addr:") != -1){
					hostIp = tmpHostIp.split("inet addr:")[1].split(" ")[0]
				}
				hostInfo['hostIp'] = hostIp
				//Get jetty port
				def commandJport = command + 'cat ' + decidedWiperdog_path +'/etc/system.properties'
				proc = commandJport.execute()
				proc.waitFor()
				def tmpHostPort = proc.in.text
				def hostPort = ""
				if(tmpHostPort != null && tmpHostPort.indexOf("jetty.port=") != -1){
					hostPort = tmpHostPort.split("jetty.port=")[1].split("\n")[0]
				}
				hostInfo['hostPort'] = hostPort
				def builder = new JsonBuilder(hostInfo)
				println builder.toPrettyString()
			}else if(cmd != null && !cmd.equals("") && cmd.equals("hostport")){
				command += 'cat ' + decidedWiperdog_path +'/etc/system.properties'
				proc = command.execute()
				proc.waitFor()
				println proc.in.text.split("jetty.port=")[1].split("\n")[0]
			}
		}else{
		    //Process for Windows
			//-- Set wiperdog PATH
			def wiperdog_path = 'E:\\wiperdog'
			Properties props = System.getProperties();
			def xwiki_dir = props.getProperty('user.dir');
			def decidedWiperdog_path
			File f = new File(xwiki_dir +"/webapps/xwiki/resources/conf.params")
			if(f.exists()){
				def shell = new GroovyShell()
				params = shell.evaluate(f)
				if(params['wiperdog_path'] != null){
					decidedWiperdog_path = params['wiperdog_path']['path']
				}else{
					decidedWiperdog_path = wiperdog_path
				}
			}else{
				decidedWiperdog_path = wiperdog_path
			}
			//-- END
			//-- Set request content type
			if(request.xpage == "plain") {
				response.setContentType('text/html')
			}
			//-- END

			def errorStr = ""
			def psExec = null
			def command = ""
			List<String> listCmd = new ArrayList<String>()
			//Get PsExec path to run remote commands
			def isLocalhost = true
			if(params['wiperdog_path']['host'] != "localhost"){
				isLocalhost = false
			}
			if(!isLocalhost){
				def prGetPsExec = 'cmd /c echo %PSTOOLS%'.execute()
				psExec = prGetPsExec.in.text.trim() + "\\PsExec.exe"
				psExec = psExec.replace("\\\\","\\")   
				if(psExec == '%PSTOOLS%'){
					println "Can not remote to: " + ['wiperdog_path']['host']
					println "Tool to remote (PsExec.exe) not found !"
					return
				}
			}
			//Run start wiperdog command
			if(cmd != null && !cmd.equals("") && cmd.equals("start")){
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,true,params)
				listCmd.add("net")
				listCmd.add("start")
				listCmd.add("wiperdog")
				def output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),true)
				if(output.exitVal != null && output.exitVal == 0){
					if(params['wiperdog_path']['host'] != "localhost"){
						println "Wiperdog service started successfully !"
					} else {
						println  output.in + "<br/>"
						println  output.err + "<br/>"
					}
				} else {
					if(params['wiperdog_path']['host'] != "localhost"){
						println "Could not start wiperdog on : !" + params['wiperdog_path']['host']
					} else {
						println "Could not start wiperdog on : !" + params['wiperdog_path']['host']
						println  output.in + "<br/>"
						println  output.err + "<br/>"

					}

				}
			}else if(cmd != null && !cmd.equals("") && cmd.equals("stop")){
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,true,params)
				listCmd.add("net")
				listCmd.add("stop")
				listCmd.add("wiperdog")
				def output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),true)
				if(output.exitVal != null && output.exitVal == 0){
					if(params['wiperdog_path']['host'] != "localhost"){
						println "Wiperdog service stopped successfully !"
					} else {
						println  output.in + "<br/>"
						println  output.err + "<br/>"
					}
				} else {
					if(params['wiperdog_path']['host'] != "localhost"){
						println "Could not stop wiperdog on : " + params['wiperdog_path']['host']
					} else {
						println "Could not stop wiperdog ! Reason: <br/> "
						println  output.in + "<br/>"
						println  output.err + "<br/>"

					}

				}

			}else if(cmd != null && !cmd.equals("") && cmd.equals("status")){
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,false,params)
				listCmd.add("sc")
				listCmd.add("query")
				listCmd.add("wiperdog")
				def output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),true)
				if(output.exitVal != null && output.exitVal == 0)
				println "<p> Information: ${output.in}</p>"
				//println "<p> Checking status: ${output.err}</p>"
				def paramObj = new GetServlet()
				def consoleServlet = paramObj.getParam('console')
				listCmd = new ArrayList<String>()
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,false,params)
				listCmd.add(decidedWiperdog_path + "\\service\\curl\\curl" )
				listCmd.add("-Is")
				listCmd.add(consoleServlet)
				output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),true)
				if(output.exitVal != null && output.exitVal == 0) {
					println "<p> Checking echo servlet: <br/> ${output.in}</p>"
				} else {
					println "<p>Failed to check echo servlet ! : <br/> ${output.in}</p>" 
				}
			}else if(cmd != null && !cmd.equals("") && cmd.equals("hostinfo")){
				 def hostInfo = [:]
				//Get host name
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,false,params)
				listCmd.add("hostname")
				def output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),true)
				def hostName = output.in
				def newHostName = hostName.replace("\r", "")
				hostName = newHostName.replace("\n","")
				hostInfo['hostName'] = hostName.replace("<br/>","")
			    //Get host IP
				listCmd = new ArrayList<String>()
				listCmd = csLib.buildCmdList(isLocalhost,psExec,listCmd,false,params)
				listCmd.add("cmd")
				listCmd.add("/c")
				listCmd.add("ipconfig")
				output =  csLib.runProcClosure(listCmd,new File(System.getProperty("user.dir")),false)
				def tmpHostIp = output.in
				def hostIp = ''
				if(tmpHostIp != null && tmpHostIp.indexOf("Local Area Connection") != -1){
					hostIp = tmpHostIp.split("Local Area Connection")[1].split("IP Address")[1].split("<br/><br/>")[0].split(":")[1].trim()
									hostInfo['hostIp'] = hostIp 
				}

				 //-- Get Jetty port from wiperdog properties file
				if(!isLocalhost){
									def rootDir = decidedWiperdog_path.substring(0,decidedWiperdog_path.indexOf(":"))
									def cmdNetUse = "net use \\\\"+params['wiperdog_path']['host']+"\\"+rootDir + "\$" + " /user:"+params['wiperdog_path']['user']+" "+params['wiperdog_path']['pass']
									def proc = cmdNetUse.execute()
									def tmpWiperdog_path = decidedWiperdog_path.replace(":\\","\$\\")
									def cmdCopy = "cmd /c copy /Y \\\\" +params['wiperdog_path']['host']+"\\"+ tmpWiperdog_path+"\\etc\\system.properties ."
									proc = cmdCopy.execute() 
									File sysProps = new File(".\\system.properties")
									if(sysProps.exists()){
										hostInfo['hostPort'] = sysProps.getText().split("jetty.port=")[1].split("\n")[0]    
										sysProps.delete() 
									} else {
										hostInfo['hostPort'] = ""
									}
							 } else {
					Properties jettyProps = new Properties()
					File propsFile = new File(decidedWiperdog_path +'/etc/system.properties')
					jettyProps.load(propsFile.newDataInputStream())
					def hostPort = jettyProps.getProperty('jetty.port')

					hostInfo['hostPort'] = hostPort
				 }
				 def builder = new JsonBuilder(hostInfo)
				 println builder.toPrettyString()


			}else if(cmd != null && !cmd.equals("") && cmd.equals("hostport")){
				//-- Get Jetty port from wiperdog properties
				Properties jettyProps = new Properties()
				File propsFile = new File(decidedWiperdog_path +'/etc/system.properties')
				jettyProps.load(propsFile.newDataInputStream())
				def hostPort = jettyProps.getProperty('jetty.port')
				println hostPort
			}
		}
	}
}catch(Exception ex){
	println ex
}


